mapper.update:
UPDATE communityReply
SET content = #{content},
    modify_date = SYSDATE,
    showReply = #{showReply},
    showCommunityReply = #{showCommunityReply}
WHERE replyNum = #{replyNum}

UPDATE communityReply
SET orderNo = #{orderNo}
WHERE replyNum = #{replyNum}

mapper.select:
SELECT COUNT(*) 
FROM communityReply


     AND communityNum = #{communityNum}

SELECT replyNum, communityNum, studentId, content, TO_CHAR(reg_date, 'YYYY-MM-DD') AS reg_date, TO_CHAR(modify_date, 'YYYY-MM-DD') AS modify_date, parentNum, showReply, showCommunityReply FROM communityReply
AND communityNum = #{communityNum}
ORDER BY replyNum ASC

mapper.update:
UPDATE communityReply
SET hitCount = hitCount + 1
WHERE replyNum = #{boardNum}

mapper.select:
SELECT *
FROM (
  SELECT *
  FROM communityReply


        AND replyNum < #{replyNum}


        AND communityNum = #{communityNum}


  ORDER BY replyNum DESC
)
WHERE ROWNUM = 1

SELECT *
FROM (
  SELECT *
  FROM communityReply


        AND replyNum > #{replyNum}


        AND communityNum = #{communityNum}


  ORDER BY replyNum ASC
)
WHERE ROWNUM = 1
