mapper.select:
SELECT gradeGuideNum, subject, content, hitCount, saveFilename, originalFilename, TO_CHAR(reg_date, 'YYYY-MM-DD') AS reg_date, TO_CHAR(modify_date, 'YYYY-MM-DD') AS modify_date, showGradeGuide, schoolId, categoryId FROM gradeGuide
AND schoolId = #{schoolId}
AND categoryId = #{categoryId}
AND subject LIKE '%' || #{keyword} || '%'
ORDER BY gradeGuideNum DESC

SELECT COUNT(*)
FROM gradeGuide


    AND schoolId = #{schoolId}


    AND categoryId = #{categoryId}


    AND subject LIKE '%' || #{keyword} || '%'

mapper.update:
UPDATE gradeGuide
SET hitCount = hitCount + 1
WHERE gradeGuideNum = #{gradeGuideNum}
