SELECT studentScheduleNum, subject, color, startDate, endDate, startTime, endTime, repeat, repeat_cycle, memo, TO_CHAR(reg_date, 'YYYY-MM-DD') AS reg_date, teacherId, studentId, categoryId FROM studentSchedule
AND studentId = #{studentId}
AND teacherId = #{teacherId}
AND subject LIKE '%' || #{keyword} || '%'
ORDER BY studentScheduleNum DESC

SELECT COUNT(*)
FROM studentSchedule


    AND studentId = #{studentId}


    AND teacherId = #{teacherId}


    AND subject LIKE '%' || #{keyword} || '%'
